import React from "react";
import Slider from "react-slick";
import styled from "styled-components";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import anupama from "../Slider/anupama.webp";
import atrangi from "../Slider/atrangi.webp";
import drama from "../Slider/drama.webp";
import imlie from "../Slider/imlie.webp";
import serial from "../Slider/serial.webp";

const ImgSlider = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
  };
  return (
    <Carousel {...settings}>
      <Wrap>
        <img src={anupama} alt="Im" />
      </Wrap>
      <Wrap>
        <img src={atrangi} alt="Im" />
      </Wrap>
      <Wrap>
        <img src={drama} alt="Im" />
      </Wrap>
      <Wrap>
        <img src={serial} alt="Im" />
      </Wrap>
      <Wrap>
        <img src={imlie} alt="Im" />
      </Wrap>
    </Carousel>
  );
};

export default ImgSlider;
const Carousel = styled(Slider)`
  margin-top: 20px;

  .slick-list {
    overflow: visible;
  }
  ul li button {
    &:before {
      font-size: 10px;
      color: rgb(150, 158, 171);
    }
  }
  li.slick-active button:before {
    color: white;
  }
  button {
    z-index: 1;
  }
  .slick-arrow {
  }
  .slick-next {
    right: -18px;
  }
  .slick-prev {
    left: -40px;
  }
  .slick-prev:before,
  .slick-next:before {
    font-family: "slick";
    font-size: 45px;
    line-height: 1;
    opacity: 0.4;
    color: #ffffffcc;
  }
`;
const Wrap = styled.div`
  padding: 10px;
  cursor: pointer;
  height: 444px;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    border-radius: 4px;
    box-shadow: rgb(0 0 0/ 69%) 0px 26px 30px -10px,
      rgb(0 0 0 / 73%) 0px 16px 10px -10px;
  }
`;
