import React from "react";
import Slider from "react-slick";
import styled from "styled-components";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import img from "../images/anatomy.webp";
import img1 from "../images/aos.webp";
import data from "../components/sliderData";

const ImgSlider = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
  };
  return (
    <Carousel {...settings}>
      <Wrap>
        <div>
          <h1>Hello Everyone</h1>
        </div>
        <div>
          <img src={img} alt="Im" />
        </div>
      </Wrap>
      <Wrap>
        <div>
          <h1>Hello Everyone</h1>
        </div>
        <div>
          <img src={img} alt="Im" />
        </div>
      </Wrap>
    </Carousel>
  );
};

export default ImgSlider;
const Carousel = styled(Slider)`
  margin-top: 20px;
  .slick-list {
    overflow: visible;
  }
  ul li button {
    &:before {
      font-size: 10px;
      color: rgb(150, 158, 171);
    }
  }
  li.slick-active button:before {
    color: white;
  }
  button {
    z-index: 1;
  }
  .slick-arrow {
  }
  .slick-next {
    right: -49px;
  }
  .slick-prev {
    left: -49px;
  }
  .slick-prev:before,
  .slick-next:before {
    font-family: "slick";
    font-size: 45px;
    line-height: 1;
    opacity: 0.4;
    color: #ffffffcc;
  }
`;
const Wrap = styled.div`
  display: flex;
  flex-diretion: row;

  cursor: pointer;
  height: 444px;
  img {
    width: 100%;
    height: 100%;
    border-left: 4px solid transparent;
    border-right: 4px solid transparent;
    border-radius: 4px;
    box-shadow: rgb(0 0 0/ 69%) 0px 26px 30px -10px,
      rgb(0 0 0 / 73%) 0px 16px 10px -10px;
  }
`;
