import React from "react";
import styled from "styled-components";
import img from "../images/disney-hotstar-logo-dark.svg";
import MenuIcon from "@mui/icons-material/Menu";
import SearchIcon from "@mui/icons-material/Search";
const Header = () => {
  return (
    <>
      <MainHeader>
        <NavContainer>
          <LeftMenu>
            <MenuBar>
              <MenuIcon />
            </MenuBar>
            <Logo>
              <img src={img} alt="asa" />
            </Logo>
            <NavLinks>
              <a href="#">TV</a>
              <a href="#">Movies</a>
              <a href="#">Sports</a>
              <a href="#">Disney+</a>
              <a href="#">Kids</a>
            </NavLinks>
          </LeftMenu>
          <RightMenu>
            <SearchBar>
              <InputField>
                <input type="text" name="" id="" />
              </InputField>
              <InputIcon>
                <SearchIcon />
              </InputIcon>
            </SearchBar>
          </RightMenu>
        </NavContainer>
      </MainHeader>
    </>
  );
};

export default Header;
const MainHeader = styled.div`
  position: fixed;
  margin: 0 auto;
  top: 0;
  left: 0;
  max-width: 100%;
  width: 100%;
  z-index: 999999;
  font-family: "Roboto", "HelveticaNeue-Light", sans-serif;
  background-color: #121926;
`;

const NavContainer = styled.div`
  display: flex;
  width: 100vw;
  flex-direction: row;
  ${"" /* justify-content: space-evenly; */}
  align-items: center;
  padding: 0 50px;
  margin: 0 auto;
  margin-top: 6px;
  min-height: 66px;
`;
const LeftMenu = styled.div`
  width: 50%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;
const NavLinks = styled.div`
  width: 70%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;

  a {
    text-decoration: none;
    color: #ffffffcc;
    font-size: 16px;
  }
`;

const MenuBar = styled(MenuIcon)`
  color: #ffffff99;
  font-size: 50px;
  font-weight: bold;
  cursor: pointer;
`;
const Logo = styled.div`
  padding-bottom: 10px;
  cursor: pointer;
`;
const RightMenu = styled.div`
  width: 50%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-around;
`;

const SearchBar = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  border-bottom: 1px solid #ffffffcc;
`;
const InputField = styled.div`
  input {
    border: none;
    outline: none;
    background-color: #121926;
  }
`;
const InputIcon = styled(SearchIcon)`
  color: #ffffffcc;
`;
