import React from "react";
import styled from "styled-components";
import img from "../images/disney-hotstar-logo-dark.svg";
import kids from "../images/kids.svg";
import MenuIcon from "@mui/icons-material/Menu";
import SearchIcon from "@mui/icons-material/Search";
const Header = () => {
  return (
    <>
      <MainHeader>
        <NavContainer>
          <LeftMenu>
            <MenuBar>
              <MenuIcon />
            </MenuBar>
            <Logo>
              <img src={img} alt="asa" />
            </Logo>
            <NavLinks>
              <a href="#">TV</a>
              <a href="#">Movies</a>
              <a href="#">Sports</a>
              <a href="#">Disney+</a>
              <a href="#">
                <img src={kids} alt="kids" />
              </a>
            </NavLinks>
          </LeftMenu>
          <RightMenu>
            <SearchDiv>
              <SearchBar>
                <InputField>
                  <input type="text" name="" placeholder="Search" id="" />
                </InputField>
                <InputIcon>
                  <SearchIcon />
                </InputIcon>
              </SearchBar>
            </SearchDiv>
            <SubButton>
              <button>SUBSCRIBE</button>
            </SubButton>
            <Login>
              <a href="#">LOGIN</a>
            </Login>
          </RightMenu>
        </NavContainer>
      </MainHeader>
    </>
  );
};

export default Header;
const MainHeader = styled.div`
  position: fixed;
  margin: 0 auto;
  top: 0;
  left: 0;
  max-width: 100%;
  width: 100%;
  z-index: 999999;
  font-family: "Roboto", "HelveticaNeue-Light", sans-serif;
  background-color: #121926;
`;

const NavContainer = styled.div`
  display: flex;
  width: 100vw;
  flex-direction: row;
  ${"" /* justify-content: space-evenly; */}
  align-items: center;
  padding: 0 50px;
  margin: 0 auto;
  margin-top: 6px;
  min-height: 66px;
`;
const LeftMenu = styled.div`
  width: 50%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;
const NavLinks = styled.div`
  width: 70%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;

  a {
    text-decoration: none;
    color: #ffffffcc;
    font-size: 16px;
  }
`;

const MenuBar = styled(MenuIcon)`
  color: #ffffff99;
  font-size: 50px;
  font-weight: bold;
  cursor: pointer;
`;
const Logo = styled.div`
  padding-bottom: 10px;
  cursor: pointer;
`;
const RightMenu = styled.div`
  width: 50%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-around;
`;
const SearchDiv = styled.div`
  width: 60%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-end;
`;
const SearchBar = styled.div`
  width: 240px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  border-bottom: 1.5px solid #ffffff99;
`;
const InputField = styled.div`
  input {
    border: none;
    outline: none;
    min-height: 32px;

    background-color: #121926;
    &::placeholder {
      font-size: 18px;
      font-weight: normal;
      color: #ffffffcc;
    }
  }
`;
const InputIcon = styled(SearchIcon)`
  color: #ffffff99;
`;
const SubButton = styled.div`
  width: 20%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-end;

  button {
    color: #ffffff;
    font-size: 13px;
    background: #1f80e0;
    padding: 6px 12px;
    font-weight: 600;
    border: #1f80e0;
    outline: none;
    border-radius: 5px;
    text-transform: capitalize;
  }
`;
const Login = styled.div`
  width: 10%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-end;
  a {
    text-decoration: none;
    color: #ffffffcc;
    font-size: 14px;
  }
`;
